import React, { useState } from "react";
import PropTypes from "prop-types";

export default function TextFrom(props) {
  const [text, setText] = useState("enter the text");
  const clicked = () => {
    // console.log("button is clicked " + text);
    let newtext = text.toUpperCase();
    setText(newtext);
  };
  const changehasdone = (event) => {
    console.log("this is change ");
    setText(event.target.value);
  };
  const lowercase = () => {
    // console.log("button has been clicked " + text);
    let newtext = text.toLowerCase();
    setText(newtext);
  };
  return (
    <div>
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea
          className="form-control"
          value={text}
          onChange={changehasdone}
          id="mybox"
          rows="8"
        ></textarea>
        <button className="btn-btn-primary" onClick={clicked}>
          Convert to uppercase
        </button>
        <button className="lowercase" onClick={lowercase}>
          Convert to lowercase
        </button>
      </div>
    </div>
  );
}
TextFrom.prototype = {
  heading: PropTypes.string.isRequired,
};
TextFrom.defaultProps = {
  heading: "enter text here ",
};
